  let X = [];
  let Y = [];
  for(let i=0;i<valNum;i++){
    X.push(posArray[i].x);
    Y.push(posArray[i].y);
  }
  let T = myRange(0,valNum);
  let aX = ODLR(T,X).a;
  let aY = ODLR(T,Y).a;
  return {x:aX,y:aY};//b we don't care about
}
function ODLR(X,Y){
